import React, { useState } from 'react';
import { TextField, Button, Typography, Container, Box, Alert } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
  formContainer: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    padding: theme.spacing(3),
    backgroundColor: '#f5f5f5', // Light background for form container
    borderRadius: '8px',
    boxShadow: '0 3px 10px rgba(0, 0, 0, 0.2)', // Adds shadow for depth
  },
  form: {
    width: '100%', // Make the form take full width of container
    marginTop: theme.spacing(3),
  },
  inputField: {
    marginBottom: theme.spacing(2), // Adds space between fields
  },
  submitButton: {
    marginTop: theme.spacing(3),
    backgroundColor: '#2E3B55',
    color: '#fff',
    '&:hover': {
      backgroundColor: '#1c2a3e',
    },
  },
  title: {
    marginBottom: theme.spacing(2),
    color: '#2E3B55', // Custom color for title
    fontWeight: 'bold',
  },
}));

const Login = () => {
  const classes = useStyles();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      const data = await response.json();

      if (response.ok) {
        localStorage.setItem('token', data.token); 
        alert('Login successful');
        window.location.href = '/';
      } else {
        setError('Login failed: ' + data.message);
      }
    } catch (error) {
      setError('An error occurred during login.');
    }
  };

  return (
    <Container maxWidth="xs">
      <Box className={classes.formContainer}>
        <Typography variant="h4" className={classes.title}>
          Login
        </Typography>
        {error && <Alert severity="error">{error}</Alert>}
        <form onSubmit={handleLogin} className={classes.form}>
          <TextField
            label="Email"
            type="email"
            fullWidth
            variant="outlined"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className={classes.inputField}
          />
          <TextField
            label="Password"
            type="password"
            fullWidth
            variant="outlined"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            className={classes.inputField}
          />
          <Button
            type="submit"
            fullWidth
            variant="contained"
            className={classes.submitButton}
          >
            Login
          </Button>
        </form>
      </Box>
    </Container>
  );
};

export default Login;
