import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MapSearch = () => {
    const [location, setLocation] = useState('');
    const [weather, setWeather] = useState(null);
    const [map, setMap] = useState(null);
    const [marker, setMarker] = useState(null);

    // Initialize Google Map
    useEffect(() => {
        const googleMap = new window.google.maps.Map(document.getElementById('map'), {
            center: { lat: 40.7128, lng: -74.0060 }, // Default  New York City
            zoom: 8,
        });
        setMap(googleMap);
    }, []);

    const handleSearch = async () => {
        try {

            const response = await axios.get(`/api/weather/${location}`);
            setWeather(response.data);


            const geocoder = new window.google.maps.Geocoder();
            geocoder.geocode({ address: location }, (results, status) => {
                if (status === 'OK') {
                    const newLocation = results[0].geometry.location;

                    map.setCenter(newLocation);

                    // Remove the previous marker, if any
                    if (marker) marker.setMap(null);

                    // Drop a new pin
                    const newMarker = new window.google.maps.Marker({
                        position: newLocation,
                        map: map,
                        title: location,
                    });

                    setMarker(newMarker);
                } else {
                    console.error('Geocode was not successful for the following reason: ' + status);
                }
            });
        } catch (error) {
            console.error('Error fetching weather:', error);
        }
    };

    return (
        <div>
            <div style={{ marginBottom: '10px' }}>
                <input
                    type="text"
                    value={location}
                    onChange={(e) => setLocation(e.target.value)}
                    placeholder="Enter city or country"
                    style={{ padding: '8px', width: '250px', marginRight: '10px' }}
                />
                <button onClick={handleSearch} style={{ padding: '8px' }}>
                    Search
                </button>
            </div>


            <div id="map" style={{ width: '100%', height: '400px', marginBottom: '20px' }}></div>


            {weather && (
                <div>
                    <h3>Weather Information for {location}</h3>
                    <p>Temperature: {weather.main.temp}Â°C</p>
                    <p>Conditions: {weather.weather[0].description}</p>
                    {/* Add more weather details */}
                </div>
            )}
        </div>
    );
};

export default MapSearch;
